package stringParameters;

public class Stringparameters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String name= "Harrydbvds sdrfh";
System.out.println(+ name.length());
System.out.println(name.toLowerCase());
System.out.println(name.toUpperCase());
System.out.println(name.trim());
System.out.println(name.substring(3));
System.out.println(name.substring(2,4));
System.out.println(name.replace('r','p'));
System.out.println(name.startsWith("Har"));
System.out.println(name.endsWith("asf"));
System.out.println(name.indexOf("r"));
System.out.println(name.lastIndexOf("r",2));
System.out.println(name.equals("Harrydbvds sdrfh"));
System.out.println(name.equalsIgnoreCase("hARrydbvds sdrfh"));



	}

}
